pragma solidity ^0.5.5;


import "./utility/StoringCreationMeta.sol";
import "./utility/Approvable.sol";
import "./UserRoles.sol";

contract EventRegistry is StoringCreationMeta, Approvable {
    address public userRoles;

    // Specifies whether only allowed event creators are able to create events
    bool public restricted;

    // Prevents any new events to be added
    bool public locked = false;

    // Specifies the min & max accepted versions of VerityEvent FOR NEW EVENTS
    // !! Change responsibly !!
    uint[2] acceptedVersions = [1, 1];

    address[] verityEvents;
    mapping(address => bool) verityEventsMap;

    mapping(address => address[]) userEvents;

    event NewVerityEvent(address eventAddress);
    event LockStatusChange(bool indexed _lock);
    event AcceptedVersionsChange(uint indexed _min, uint indexed _max);

    constructor(bool _restricted, address _userRoles) public {
        userRoles = _userRoles;
        restricted = _restricted;
    }

    function registerEvent(uint _version) public {
        require(
            _isAcceptedVersion(_version),
            "Version not accepted."
        );
        require(
            locked == false,
            "EventRegistry is locked"
        );

        if (restricted) {
            require(
                UserRoles(userRoles).hasRole(tx.origin, 1),
                "Creator not permitted to register event."
            );
        }
        verityEvents.push(msg.sender);
        verityEventsMap[msg.sender] = true;
        emit NewVerityEvent(msg.sender);
    }

    function setAcceptedVersions(uint _min, uint _max) public onlyApproved {
        require(
            _min <= _max,
            "Min must be minier than max."
        );
        acceptedVersions = [_min, _max];
        emit AcceptedVersionsChange(_min, _max);
    }

    function getAcceptedVersions() public view returns(uint[2] memory) {
        return acceptedVersions;
    }

    function _isAcceptedVersion(uint _version) private view returns(bool) {
        return _version >= acceptedVersions[0] &&
                _version <= acceptedVersions[1];
    }

    function lockRegistry(bool _locked) public onlyApproved {
        locked = _locked;
        emit LockStatusChange(locked);
    }

    function setRestricted(bool _restricted) public onlyApproved {
        restricted = _restricted;
    }

    function setUserRoles(address _userRoles) public onlyApproved {
        userRoles = _userRoles;
    }

    function getUserEvents() public view returns(address[] memory) {
        return userEvents[msg.sender];
    }

    function addEventToUser(address _user) external {
        require(verityEventsMap[msg.sender]);

        userEvents[_user].push(msg.sender);
    }

    function getEventsLength() public view returns(uint) {
        return verityEvents.length;
    }

    function getEventsByIds(uint[] memory _ids) public view returns(uint[] memory, address[] memory) {
        address[] memory _events = new address[](_ids.length);

        for(uint i = 0; i < _ids.length; ++i) {
            _events[i] = verityEvents[_ids[i]];
        }

        return (_ids, _events);
    }

    function getUserEventsLength(address _user)
        public
        view
        returns(uint)
    {
        return userEvents[_user].length;
    }

    function getUserEventsByIds(address _user, uint[] memory _ids)
        public
        view
        returns(uint[] memory, address[] memory)
    {
        address[] memory _events = new address[](_ids.length);

        for(uint i = 0; i < _ids.length; ++i) {
            _events[i] = userEvents[_user][_ids[i]];
        }

        return (_ids, _events);
    }
}
