pragma solidity ^0.5.0;


contract UserRoles {
    address public owner;

    mapping(address => mapping(uint => bool)) userRoles;

    modifier onlyOwner {
        require(msg.sender == owner);
        _;
    }

    constructor() public {
        owner = msg.sender;
    }

    function setRole(address _user, uint _role) public onlyOwner {
        userRoles[_user][_role] = true;
    }

    function setRoles(address _user, uint[] memory _roles) public onlyOwner {
        for(uint i = 0; i < _roles.length; ++i) {
            userRoles[_user][_roles[i]] = true;
        }
    }

    function removeRole(address _user, uint _role) public onlyOwner {
        userRoles[_user][_role] = false;
    }

    function removeRoles(address _user, uint[] memory _roles) public onlyOwner {
        for(uint i = 0; i < _roles.length; ++i) {
            userRoles[_user][_roles[i]] = false;
        }
    }

    function hasRole(address _user, uint _role) public view returns(bool) {
        return userRoles[_user][_role];
    }

    function hasAnyRole(address _user, uint[] memory _roles) public view returns(bool) {
        for(uint i = 0; i < _roles.length; ++i) {
            if(userRoles[_user][_roles[i]]) {
                return true;
            }
        }
        return false;
    }
}
