pragma solidity ^0.5.5;

import "./VerityToken.sol";
import "./verity_event/Store.sol";
import "./utility/Proxy.sol";


interface UserRolesI {
    function hasAnyRole(address _user, uint[] calldata _roles) external view returns (bool);
}

contract VerityEventProxy is Store, Proxy {

    constructor(
        string memory _eventName,
        uint _applicationStartTime,
        uint _applicationEndTime,
        uint _eventStartTime,
        uint _eventRunTime, // in seconds
        address[4] memory _cAddresses, // [tokenAddress, eventRegistry, userRoles, implementation]
        address[] memory _eventResolvers,
        uint _leftoversRecoverableAfter, // time duration after eventEndTime when event is marker as failed (in seconds)
        uint[6] memory _consensusRules, // [minTotalVotes, minConsensusVotes, minConsensusRatio, minParticipantRatio, maxParticipants, distribution]
        uint _stakingAmount,
        uint[4] memory _disputeRules, // [dispute amount, dispute timeout, dispute multiplier, default extension in seconds]
        string memory _ipfsEventHash,
        uint[] memory _requiredUserRoles,
        bytes32[2] memory _eventCategory,
        uint _nodeRewardsPool
    )
        public
        payable
    {
        require(_applicationStartTime < _applicationEndTime);
        require(_eventStartTime > _applicationEndTime, "Event can't start before applications close.");
        require(msg.value >= _nodeRewardsPool, "Not enough value sent for specified rewards pool");

        tokenAddress = _cAddresses[0];
        eventRegistryAddress = _cAddresses[1];
        EventRegistryI(eventRegistryAddress).registerEvent(version);
        permissionRegistry = _cAddresses[2];
        implementation = _cAddresses[3];

        nodeRewardsPool = nodeRewardsBalance = _nodeRewardsPool;

        applicationStartTime = _applicationStartTime;
        applicationEndTime = _applicationEndTime;

        eventName = _eventName;
        eventCategory = _eventCategory;
        eventStartTime = _eventStartTime;
        eventEndTime = _eventStartTime + _eventRunTime;

        eventResolvers = _eventResolvers;

        owner = msg.sender;
        leftoversRecoverableAfter = _leftoversRecoverableAfter;

        rewardsValidationRound = 1;
        rewardsValidation.currentMasterNode = eventResolvers[0];

        stakingAmount = _stakingAmount;

        ipfsEventHash = _ipfsEventHash;

        setConsensusRules(_consensusRules);
        setDisputeData(_disputeRules);

        requiredUserRoles = _requiredUserRoles;
    }

    /// Sets consensus rules. For internal use only.
    function setConsensusRules(uint[6] memory rules) private {
        consensusRules.minTotalVotes = rules[0];
        consensusRules.minConsensusVotes = rules[1];
        consensusRules.minConsensusRatio = rules[2];
        consensusRules.minParticipantRatio = rules[3];
        consensusRules.maxParticipants = rules[4];
        consensusRules.rewardsDistribution = RewardsDistribution(rules[5]);
    }

    function setDisputeData(uint[4] memory rules) private {
        uint _multiplier = rules[2];
        uint _extension = rules[3];
        if (_multiplier <= 1) {
            _multiplier = 1;
        }

        if (_extension == 0) {
            _extension = 21600; // 6h in seconds
        }

        dispute.amount = rules[0];
        dispute.timeout = rules[1];
        dispute.multiplier = _multiplier;
        dispute.round = 0;
        disputeTimeExtension = _extension;
    }
}
